import React, { useState } from "react";
import styled from "styled-components";
function App() {
  const [isPlay, setIsPlay] = useState<boolean>(false); // 타이머의 동작 상태 유무
  const [hour, setHour] = useState<number>(0); //시
  const [minute, setMinute] = useState<number>(0); //분
  const [second, setSecond] = useState<number>(0); //초
  const handlePlay = () => {};
  const handleHourChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.preventDefault();
    if (Number(e.target.value) <= 24 && Number(e.target.value) >= 0) {
      // 시 : 0 ~ 24
      setHour(Number(e.target.value));
    }
  };
  const handleMinuteChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    e.preventDefault();
    if (Number(e.target.value) <= 60 && Number(e.target.value) >= 0) {
      // 시 : 0 ~ 24
      setHour(Number(e.target.value));
    }
  };

  return (
    <Wrapper>
      <TitleSection>타이머</TitleSection>
      <TimeSection>
        <SubtitleSection>시</SubtitleSection>
        <Hour onChange={handleHourChange}></Hour>
        <SubtitleSection>분</SubtitleSection>
        <Minute onChange={handleMinuteChange}></Minute>
        <SubtitleSection>초</SubtitleSection>
        <Second onChange={handleSecondChange}></Second>
      </TimeSection>
      <ButtonSection>
        <PlayButton>시작</PlayButton>
        <StopButton>멈춤</StopButton>
        <InitButton>초기화</InitButton>
      </ButtonSection>
    </Wrapper>
  );
}

export default App;
const Wrapper = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
`;
const TitleSection = styled.div`
  height: 5rem;
`;
const SubtitleSection = styled.div`
  margin: 0rem 1rem;
`;
const TimeSection = styled.section`
  display: flex;
`;
const Hour = styled.input``;
const Minute = styled.input``;
const Second = styled.input``;
const ButtonSection = styled.section`
  margin: 1rem;
`;
const PlayButton = styled.button`
  width: 3rem;
  height: 3rem;
  border: none;
  border-radius: 3rem;
`;
const StopButton = styled.button`
  width: 3rem;
  height: 3rem;
  border: none;
  border-radius: 3rem;
`;
const InitButton = styled.button`
  width: 3rem;
  height: 3rem;
  border: none;
  border-radius: 3rem;
`;
